import React, { useState } from 'react';
import { useToast, VStack, FormControl, FormLabel, Input, InputGroup, InputRightElement, Button } from '@chakra-ui/react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Updated import statement
import '../Home.css'; // Import the CSS file
const Login = () => {
  const [show, setShow] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const toast = useToast();
  const navigate = useNavigate(); // Updated hook

  const handleClick = () => setShow(!show);

const submitHandler = async () => {
  setLoading(true);

  if (!email || !password) {
    toast({
      title: 'Please Fill all the Fields',
      status: 'warning',
      duration: 5000,
      isClosable: true,
      position: 'bottom',
    });
    setLoading(false);
    return;
  }

  try {
    const config = {
      headers: {
        'Content-type': 'application/json',
      },
    };

    const response = await axios.post(
      'http://localhost:5000/api/user/login',
      {
        email,
        password,
      },
      config
    );

    const data = response.data;

    if (!data.success) {
      toast({
        title: 'Login Failed',
        description: 'Incorrect email or password',
        status: 'error',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
      setLoading(false);
      return;
    }

    toast({
      title: 'Login Successful',
      status: 'success',
      duration: 5000,
      isClosable: true,
      position: 'bottom',
    });

    localStorage.setItem('userInfo', JSON.stringify(data));
    setLoading(false);
    navigate('/library');
  } catch (error) {
    toast({
      title: 'Error Occurred!',
      description: error.response.data.message,
      status: 'error',
      duration: 5000,
      isClosable: true,
      position: 'bottom',
    });
    setLoading(false);
  }
};


  return (
    <VStack spacing="5" color="black" width="100%" maxW="400px" marginX="auto">
      <FormControl id="email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
          placeholder="Enter Your Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </FormControl>
      <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <InputGroup>
          <Input
            type={show ? 'text' : 'password'}
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <InputRightElement>
            <Button h="1.75rem" size="sm" onClick={handleClick}>
              {show ? 'Hide' : 'Show'}
            </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
      <Button
        colorScheme="blue"
        width="100%"
        style={{ marginTop: 15 }}
        onClick={submitHandler}
        isLoading={loading}
      >
        Login
      </Button>
      <Button
        variant="solid"
        colorScheme="green"
        width="100%"
        onClick={() => {
          setEmail('guest@example.com');
          setPassword('123456');
        }}
      >
        Get User Credentials
      </Button>
    </VStack>
  );
};

export default Login;
