// components/Library.js

import React, { useState, useEffect } from 'react';
import { VStack, Heading, HStack, Button } from '@chakra-ui/react';
import MyBooks from './MyBooks';
import Google from './Google';
import Logout from './Authentication/Logout';
import AdminDashboard from './AdminDashboard'; // Import the AdminDashboard component

const Library = () => {
  const [activeComponent, setActiveComponent] = useState('MyBooks');
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('userInfo'));
    const userIsAdmin = userData && userData.isAdmin; // Assuming the user info has the 'isAdmin' property

    setIsAdmin(userIsAdmin);
  }, []); // Empty dependency array to run the effect only once when the component mounts

  const handleButtonClick = (component) => {
    setActiveComponent(component);
  };

  const handleTransaction = async (bookId, transactionType) => {
    try {
      // Make an API request to your backend to update the transaction
      const response = await fetch(`/api/transactions/${bookId}`, {
        method: 'POST', // or 'PUT' depending on your API design
        headers: {
          'Content-Type': 'application/json',
          // Add any additional headers as needed, e.g., authentication token
        },
        body: JSON.stringify({ transactionType }),
      });

      if (response.ok) {
        // Successfully updated the transaction
        console.log(`Transaction: ${transactionType} for Book ID ${bookId} successful.`);
        // Optionally, you can update the UI to reflect the transaction
      } else {
        // Handle error responses from the API
        console.error(`Transaction failed for Book ID ${bookId}.`);
      }
    } catch (error) {
      // Handle network or other errors
      console.error(`Error during transaction for Book ID ${bookId}:`, error);
    }
  };

  return (
<VStack spacing={{ base: 4, md: 8 }} width="100%" maxW="1000px" marginX="auto">
      <HStack spacing="5">
        <Button
          colorScheme={activeComponent === 'MyBooks' ? 'blue' : 'gray'}
          onClick={() => handleButtonClick('MyBooks')}
        >
          My Books
        </Button>
        <Button
          colorScheme={activeComponent === 'Google' ? 'green' : 'gray'}
          onClick={() => handleButtonClick('Google')}
        >
          Google Books
        </Button>
        <Logout />
      </HStack>
      <Heading as="h1" size="xl">
        {isAdmin ? 'Admin Dashboard' : ''}
      </Heading>
      {isAdmin ? (
        <AdminDashboard isAdmin={isAdmin} isEditable={true} />
      ) : (
        <>
          {activeComponent === 'MyBooks' && <MyBooks isEditable={true} onTransaction={handleTransaction} />}
          {activeComponent === 'Google' && <Google />}
        </>
      )}
      {isAdmin && (
        <>
          {/* Display the same content as in the library with editable availability status */}
          <MyBooks isEditable={true} onTransaction={handleTransaction} />
          {/* You can add more components below if needed */}
        </>
      )}
    </VStack>
  );
};

export default Library;
