// BookCard.js

import React from 'react';
import {
  Box,
  Heading,
  Text,
  useColorModeValue,
  Button,
  useToast,
  Toast,
} from '@chakra-ui/react';

// components/BookCard.js

// ... (other imports and code)

const BookCard = ({ book, isAdmin, onAvailabilityChange, onTransaction }) => {
    const cardBgColor = useColorModeValue('white', 'gray.700'); // Define the cardBgColor
  const handleAvailabilityChange = () => {
    const newAvailability =
      book?.availabilityStatus === 'Available' ? 'Not Available' : 'Available';
    onAvailabilityChange(book?.id, newAvailability);

    // Display a toast message
    showToast(
      `Availability for "${book?.volumeInfo?.title}" updated to ${newAvailability}`
    );
  };

  const handleTransaction = (transactionType) => {
    // Perform the transaction logic here
    onTransaction(book?.id, transactionType);
    console.log(`Transaction: ${transactionType} for Book ID ${book?.id}`);
    // Display a toast message
    showToast(
      `Transaction: ${transactionType} for "${book?.volumeInfo?.title}"`
    );
  };

  const showToast = (message) => {
    Toast({
      title: message,
      status: 'info',
      duration: 5000,
      isClosable: true,
    });
  };

  return (
    <Box
      key={book?.id}
      p="3"
      borderWidth="1px"
      borderRadius="md"
      bg={cardBgColor}
      _hover={{ shadow: 'md' }}
    >
      <Heading as="h3" size="sm" mb="2" noOfLines={2}>
        {book?.volumeInfo?.title}
      </Heading>
      {book?.volumeInfo?.authors && (
        <Text fontSize="xs" color="gray.500" noOfLines={1}>
          Author(s): {book.volumeInfo.authors.join(', ')}
        </Text>
      )}
      {book?.volumeInfo?.description && (
        <Text fontSize="xs" color="gray.500" noOfLines={3}>
          {book?.volumeInfo?.description}
        </Text>
      )}
      {book?.availabilityStatus && (
        <Text fontSize="xs" color="green.500">
          Availability: {book?.availabilityStatus}
        </Text>
      )}

      {isAdmin && (
        <>
        <Button onClick={() => onAvailabilityChange(book.id, 'Unavailable')}>
            {book?.availabilityStatus === 'Available'
              ? 'Mark as Not Available'
              : 'Mark as Available'}
          </Button>
          <Button mt="2" size="sm" onClick={() => handleTransaction('Borrow')}>
            Borrow
          </Button>
          <Button mt="2" size="sm" onClick={() => handleTransaction('Return')}>
            Return
          </Button>
        </>
      )}
    </Box>
  );
};

export default BookCard;
