import React, { useState } from 'react';
import { VStack, Heading, HStack, Button } from '@chakra-ui/react';
import MyBooks from './MyBooks';
import Google from './Google'; // Assuming your component is named Google

const Library = () => {
  const [activeComponent, setActiveComponent] = useState('MyBooks');

  const handleButtonClick = (component) => {
    setActiveComponent(component);
  };

  const handleLogout = async () => {
    try {
      // Send a request to your server to handle logout
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${yourAuthToken}`, // Include the user's authentication token
        },
      });

      if (response.ok) {
        // Clear client-side authentication token (if stored locally)
        localStorage.removeItem('authToken');

        // Redirect to the login page or perform any other client-side cleanup
        window.location.href = '/login';
      } else {
        console.error('Logout failed');
      }
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  return (
    <VStack spacing="5" width="100%" maxW="1000px" marginX="auto">
      <HStack spacing="5">
        <Button
          colorScheme={activeComponent === 'MyBooks' ? 'blue' : 'gray'}
          onClick={() => handleButtonClick('MyBooks')}
        >
          My Books
        </Button>
        <Button
          colorScheme={activeComponent === 'Google' ? 'green' : 'gray'}
          onClick={() => handleButtonClick('Google')}
        >
          Google Books
        </Button>
        <Button
          colorScheme="red"
          onClick={handleLogout}
        >
          Logout
        </Button>
      </HStack>
      <Heading as="h1" size="xl">
        {activeComponent === 'MyBooks' ? 'My Books' : 'Google Books'}
      </Heading>
      {activeComponent === 'MyBooks' && <MyBooks />}
      {activeComponent === 'Google' && <Google />}
    </VStack>
  );
};

export default Library;
