// components/AdminBooks.js
import React, { useState, useEffect } from 'react';
import { VStack, Text, Button } from '@chakra-ui/react';

const AdminBooks = () => {
  // Assume you have a list of books, each with an availability status
  const [books, setBooks] = useState([
    { id: 1, title: 'Book 1', available: true },
    { id: 2, title: 'Book 2', available: false },
    // Add more books as needed
  ]);

  // Function to toggle the availability status of a book
  const toggleAvailability = (bookId) => {
    setBooks((prevBooks) =>
      prevBooks.map((book) =>
        book.id === bookId ? { ...book, available: !book.available } : book
      )
    );
  };

  return (
    <VStack spacing="4" align="flex-start" width="100%" maxW="800px" marginX="auto">
      <Text fontSize="xl">Manage Book Availability</Text>
      {books.map((book) => (
        <HStack key={book.id} spacing="2">
          <Text>{book.title}</Text>
          <Button onClick={() => toggleAvailability(book.id)}>
            {book.available ? 'Make Unavailable' : 'Make Available'}
          </Button>
        </HStack>
      ))}
    </VStack>
  );
};

export default AdminBooks;
