import React, { useState, useEffect } from 'react';
import {
  VStack,
  Heading,
  Input,
  Button,
  SimpleGrid,
  Box,
  Text,
  useColorModeValue,
  useToast,
} from '@chakra-ui/react';

const MyBooks = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);
  const [isNotFound, setIsNotFound] = useState(false);

  const toast = useToast();

  const dummyData = [
    {
      id: 1,
      volumeInfo: {
        title: 'The Great Gatsby',
        authors: ['F. Scott Fitzgerald'],
        description:
          'The story primarily concerns the young and mysterious millionaire Jay Gatsby.',
      },
      availabilityStatus: 'Available',
    },
    {
      id: 2,
      volumeInfo: {
        title: 'To Kill a Mockingbird',
        authors: ['Harper Lee'],
        description:
          'The novel explores the irrationality of adult attitudes toward race and class.',
      },
      availabilityStatus: 'Available',
    },
    {
      id: 3,
      volumeInfo: {
        title: '1984',
        authors: ['George Orwell'],
        description: 'A dystopian novel set in Airstrip One, a province of the superstate Oceania.',
      },
      availabilityStatus: 'Available',
    },
    {
      id: 4,
      volumeInfo: {
        title: 'The Hobbit',
        authors: ['J.R.R. Tolkien'],
        description:
          'The hobbit Bilbo Baggins is persuaded by the wizard Gandalf to join a group of dwarves on a quest.',
      },
      availabilityStatus: 'Available',
    },
    {
      id: 5,
      volumeInfo: {
        title: 'Pride and Prejudice',
        authors: ['Jane Austen'],
        description:
          'The novel follows the character development of Elizabeth Bennet, the dynamic protagonist.',
      },
      availabilityStatus: 'Available',
    },
    {
      id: 6,
      volumeInfo: {
        title: 'The Catcher in the Rye',
        authors: ['J.D. Salinger'],
        description: 'A story about a teenager named Holden Caulfield in New York City.',
      },
      availabilityStatus: 'Available',
    },
  ];

  const fetchBooks = (query) => {
    // Check if the query is not an empty string
    if (query.trim() !== '') {
      const filteredBooks = dummyData.filter((book) =>
        book.volumeInfo.title.toLowerCase().includes(query.toLowerCase())
      );

      setBooks(filteredBooks);
      setIsNotFound(filteredBooks.length === 0);
    } else {
      // If the query is empty, clear books and reset isNotFound
      setBooks([]);
      setIsNotFound(false);
    }
  };

  useEffect(() => {
    fetchBooks('Classic'); // Default search term
  }, []);

  const handleSearch = () => {
    fetchBooks(searchTerm);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Clear books and reset isNotFound when the search term becomes empty
    if (value.trim() === '') {
      setBooks([]);
      setIsNotFound(false);
    }
  };

  const cardBgColor = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('black', 'white');

  useEffect(() => {
    if (isNotFound && searchTerm.trim() !== '') {
      toast({
        title: 'Books Not Found',
        description: `No books found for '${searchTerm}'.`,
        status: 'info',
        duration: 5000,
        isClosable: true,
      });
    }
  }, [isNotFound, searchTerm, toast]);

  return (
    <VStack spacing="5" color={textColor} width="100%" maxW="800px" marginX="auto">
      <Heading as="h1" size="xl">
        Library
      </Heading>
      <Input
        placeholder="Search for books..."
        value={searchTerm}
        onChange={handleInputChange}
        onKeyDown={handleKeyPress}
      />
      <Button colorScheme="blue" onClick={handleSearch}>
        Search
      </Button>
      <SimpleGrid columns={{ base: 1, sm: 2, md: 3 }} spacing={5}>
        {(isNotFound ? dummyData : books).map((book) => (
          <Box
            key={book.id}
            p="3"
            borderWidth="1px"
            borderRadius="md"
            bg={cardBgColor}
            _hover={{ shadow: 'md' }}
          >
            <Heading as="h3" size="sm" mb="2" noOfLines={2}>
              {book.volumeInfo.title}
            </Heading>
            {book.volumeInfo.authors && (
              <Text fontSize="xs" color="gray.500" noOfLines={1}>
                Author(s): {book.volumeInfo.authors.join(', ')}
              </Text>
            )}
            {book.volumeInfo.description && (
              <Text fontSize="xs" color="gray.500" noOfLines={3}>
                {book.volumeInfo.description}
              </Text>
            )}
            {book.availabilityStatus && (
              <Text fontSize="xs" color="green.500">
                Availability: {book.availabilityStatus}
              </Text>
            )}
          </Box>
        ))}
      </SimpleGrid>
      {isNotFound && (
        <Text color="red.500" textAlign="center">
          No books found. Showing all books.
        </Text>
      )}
    </VStack>
  );
};

export default MyBooks;
