// components/AdminDashboard.js

import React from 'react';
import { VStack, Text } from '@chakra-ui/react';

const AdminDashboard = ({ isAdmin, isEditable }) => {
  // Display the admin-specific content only if the user is an admin
  if (!isAdmin) {
    return (
      <VStack spacing="4" align="flex-start" width="100%" maxW="800px" marginX="auto">
        <Text fontSize="xl">You do not have access to the Admin Dashboard.</Text>
      </VStack>
    );
  }

  return (
    <VStack spacing="4" align="flex-start" width="100%" maxW="800px" marginX="auto">
      <Text fontSize="xl">Welcome to the Admin Dashboard!</Text>
      {/* Add admin-specific features or views here */}
      <Text>Admin Feature 1</Text>
      <Text>Admin Feature 2</Text>
      {isEditable && <Text>This is an editable feature for admins.</Text>}
    </VStack>
  );
};

export default AdminDashboard;
