// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './style.css';

const App = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ name: '', author: '' });

  useEffect(() => {
    // Fetch books from the API
    axios.get('/api/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error fetching books', error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewBook({ ...newBook, [name]: value });
  };

  const handleAddBook = async () => {
    try {
      // Make a POST request to add a new book
      const response = await axios.post('/api/books', newBook);
      // Update the local state with the new book from the server response
      setBooks([...books, response.data]);
      // Reset the form
      setNewBook({ name: '', author: '' });
    } catch (error) {
      console.error('Error adding book', error);
    }
  };

  return (
    <div className="app-container">
      <h2>Online Library</h2>
      <div className="book-list-container">
        <h3>Book List</h3>
        <ul>
          {books.map(book => (
            <li key={book._id}>{book.name} by {book.author}</li>
          ))}
        </ul>
      </div>
      <div className="add-book-container">
        <h3>Add a New Book</h3>
        <form>
          <label>
            Book Name:
            <input type="text" name="name" value={newBook.name} onChange={handleInputChange} />
          </label>
          <label>
            Author:
            <input type="text" name="author" value={newBook.author} onChange={handleInputChange} />
          </label>
          <button type="button" onClick={handleAddBook}>Add Book</button>
        </form>
      </div>
    </div>
  );
};

export default App;
