import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Google = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simulating a delay to mimic the loading process
  useEffect(() => {
    const delay = setTimeout(() => {
      setLoading(false);
    }, 1000); // Adjust the delay as needed

    return () => clearTimeout(delay);
  }, []);

  const fetchBooks = async (query) => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5000/api/book/fetch?searchTerm=${query}`);
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks('JavaScript');
  }, []);

  const handleSearch = () => {
    fetchBooks(searchTerm);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const cardStyle = {
    padding: '1rem',
    border: '1px solid #ccc',
    borderRadius: '4px',
    height: '150px', // Adjust the height as needed
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '1rem' }}>Library</h1>
      <input
        type="text"
        placeholder="Search for books..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyDown={handleKeyPress}
        style={{ padding: '0.5rem', marginBottom: '1rem', width: '100%' }}
      />
      <button
        style={{ padding: '0.5rem', backgroundColor: 'blue', color: 'white', cursor: 'pointer', width: '100%' }}
        onClick={handleSearch}
      >
        Search
      </button>
      {loading ? (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '1rem' }}>
          {[...Array(6)].map((_, index) => (
            <div key={index} style={{ ...cardStyle, opacity: '0.5' }}>
              <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>Loading...</h3>
              <p style={{ fontSize: '0.8rem', color: '#888', marginBottom: '0.5rem' }}>Author(s): Loading...</p>
              <p style={{ fontSize: '0.8rem', color: '#888' }}>Loading...</p>
              <p style={{ fontSize: '0.8rem', color: 'green' }}>Availability: Loading...</p>
            </div>
          ))}
        </div>
      ) : (
        books.length > 0 && (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '1rem' }}>
            {books.slice(0, 6).map((book) => (
              <div key={book.id} style={cardStyle}>
                <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>{book.volumeInfo.title}</h3>
                {book.volumeInfo.authors && (
                  <p style={{ fontSize: '0.8rem', color: '#888', marginBottom: '0.5rem' }}>
                    Author(s): {book.volumeInfo.authors.join(', ')}
                  </p>
                )}
                {book.volumeInfo.description && (
                  <p style={{ fontSize: '0.8rem', color: '#888' }}>{book.volumeInfo.description}</p>
                )}
                {book.availabilityStatus && (
                  <p style={{ fontSize: '0.8rem', color: 'green' }}>Availability: {book.availabilityStatus}</p>
                )}
              </div>
            ))}
          </div>
        )
      )}
    </div>
  );
};

export default Google;
