// BookCard.js

import React from 'react';
import {
  Box,
  Heading,
  Text,
  useColorModeValue,
  Button,
  useToast,
} from '@chakra-ui/react';

const BookCard = ({ book, onAvailabilityChange }) => {
  const cardBgColor = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('black', 'white');
  const toast = useToast();

  const handleAvailabilityChange = () => {
    const newAvailability = book.availabilityStatus === 'Available' ? 'Not Available' : 'Available';
    onAvailabilityChange(book.id, newAvailability);

    // Display a toast message
    showToast(`Availability for "${book.volumeInfo.title}" updated to ${newAvailability}`);
  };

  const showToast = (message) => {
    toast({
      title: message,
      status: 'info',
      duration: 5000,
      isClosable: true,
    });
  };

  return (
    <Box
      key={book.id}
      p="3"
      borderWidth="1px"
      borderRadius="md"
      bg={cardBgColor}
      _hover={{ shadow: 'md' }}
    >
      <Heading as="h3" size="sm" mb="2" noOfLines={2}>
        {book.volumeInfo.title}
      </Heading>
      {book.volumeInfo.authors && (
        <Text fontSize="xs" color="gray.500" noOfLines={1}>
          Author(s): {book.volumeInfo.authors.join(', ')}
        </Text>
      )}
      {book.volumeInfo.description && (
        <Text fontSize="xs" color="gray.500" noOfLines={3}>
          {book.volumeInfo.description}
        </Text>
      )}
      {book.availabilityStatus && (
        <Text fontSize="xs" color="green.500">
          Availability: {book.availabilityStatus}
        </Text>
      )}
      <Button mt="2" size="sm" onClick={handleAvailabilityChange}>
        {book.availabilityStatus === 'Available' ? 'Mark as Not Available' : 'Mark as Available'}
      </Button>
    </Box>
  );
};

export default BookCard;
