import React, { useState, useEffect } from 'react';
import { VStack, Heading, Input, Button, SimpleGrid, Box, Text } from '@chakra-ui/react';
import axios from 'axios';

const Library = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);

  const fetchBooks = async (query) => {
    try {
      const response = await axios.get(`/api/book/fetch?searchTerm=${query}`);
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error.message);
    }
  };

  // Use useEffect to fetch books when the component mounts
  useEffect(() => {
    // Fetch default books when the component mounts
    fetchBooks('JavaScript'); // You can replace 'JavaScript' with any default query
  }, []); // Empty dependency array ensures this effect runs only once when the component mounts

  const handleSearch = () => {
    // Fetch books based on the search term
    fetchBooks(searchTerm);
  };

  return (
    <VStack spacing="5" color="black" width="100%" maxW="800px" marginX="auto">
      <Heading as="h1" size="xl">
        Library
      </Heading>
      <Input
        placeholder="Search for books..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <Button colorScheme="blue" onClick={handleSearch}>
        Search
      </Button>
      {books.length > 0 && (
        <SimpleGrid columns={3} spacing={5}>
          {books.map((book) => (
            <Box key={book.id} p="5" borderWidth="1px" borderRadius="md">
              <Heading as="h3" size="md" mb="2">
                {book.volumeInfo.title}
              </Heading>
              {book.volumeInfo.authors && (
                <Text fontSize="sm" color="gray.500">
                  Author(s): {book.volumeInfo.authors.join(', ')}
                </Text>
              )}
              {book.volumeInfo.description && (
                <Text fontSize="sm" color="gray.500">
                  {book.volumeInfo.description}
                </Text>
              )}
            </Box>
          ))}
        </SimpleGrid>
      )}
    </VStack>
  );
};

export default Library;
