// components/Home.js
import React, { useState } from 'react';
import axios from 'axios';
import '../App.css'; // Adjust the path based on your file structure

const Home = () => {
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [signupData, setSignupData] = useState({ username: '', password: '' });
  const [message, setMessage] = useState('');
  const [showLogin, setShowLogin] = useState(true);

  const handleLogin = async () => {
    try {
      // Make a request to your backend API for login
      const response = await axios.post('/api/login', loginData);

      // Assuming the backend sends a token upon successful login
      const token = response.data.token;

      // Store the token in localStorage or a global state for future requests
      localStorage.setItem('token', token);

      // Optionally, redirect the user to the library/dashboard
      // Replace '/library' with the actual path of your library/dashboard
      window.location.href = '/library';
    } catch (error) {
      setMessage('Login failed. Check your credentials.');
    }
  };

  const handleSignup = async () => {
    try {
      // Make a request to your backend API for signup
      // eslint-disable-next-line
      const response = await axios.post('/api/signup', signupData);

      setMessage('Signup successful! You can now log in.');
    } catch (error) {
      setMessage('Signup failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Login/Signup Page</h2>

      {/* Buttons to toggle between login and signup */}
      <div>
        <button onClick={() => setShowLogin(true)}>Login</button>
        <button onClick={() => setShowLogin(false)}>Signup</button>
      </div>

      {/* Conditional rendering based on showLogin state */}
      {showLogin ? (
        // Login Form
        <div>
          <h3>Login</h3>
          <form>
            <label>
              Username:
              <input
                type="text"
                value={loginData.username}
                onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
              />
            </label>
            <label>
              Password:
              <input
                type="password"
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
              />
            </label>
            <button type="button" onClick={handleLogin}>
              Login
            </button>
          </form>
        </div>
      ) : (
        // Signup Form
        <div>
          <h3>Signup</h3>
          <form>
            <label>
              Username:
              <input
                type="text"
                value={signupData.username}
                onChange={(e) => setSignupData({ ...signupData, username: e.target.value })}
              />
            </label>
            <label>
              Password:
              <input
                type="password"
                value={signupData.password}
                onChange={(e) => setSignupData({ ...signupData, password: e.target.value })}
              />
            </label>
            <button type="button" onClick={handleSignup}>
              Signup
            </button>
          </form>
        </div>
      )}

      {/* Display Messages */}
      <p>{message}</p>
    </div>
  );
};

export default Home;
