import React, { useState } from 'react';
import { VStack, Heading, Input, Button, SimpleGrid, Box, Text, HStack } from '@chakra-ui/react';
import Library from './Library'; // Import your Library component
import Google from './Google'; // Import your Google component

const MainLibrary = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [showLibrary, setShowLibrary] = useState(true);

  const handleSearch = () => {
    // Implement your search logic here
    // For now, let's just log the search term
    console.log('Search term:', searchTerm);
  };

  const switchToLibrary = () => {
    setShowLibrary(true);
  };

  const switchToGoogleBooks = () => {
    setShowLibrary(false);
  };

  return (
    <VStack spacing="5" color="black" width="100%" maxW="800px" marginX="auto">
      <HStack spacing="5">
        <Button colorScheme="blue" onClick={switchToLibrary}>
          My Books
        </Button>
        <Button colorScheme="green" onClick={switchToGoogleBooks}>
          Google Books
        </Button>
      </HStack>
      {showLibrary ? (
        <Library />
      ) : (
        <Google />
      )}
    </VStack>
  );
};

export default MainLibrary;
