import React, { useState, useEffect } from 'react';
import { VStack, Heading, Input, Button, SimpleGrid, Box, Text, useColorModeValue } from '@chakra-ui/react';
import axios from 'axios';

const Google = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simulating a delay to mimic the loading process
  useEffect(() => {
    const delay = setTimeout(() => {
      setLoading(false);
    }, 2000); // Adjust the delay as needed

    return () => clearTimeout(delay);
  }, []);

  const fetchBooks = async (query) => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5000/api/book/fetch?searchTerm=${query}`);
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks('JavaScript');
  }, []);

  const handleSearch = () => {
    fetchBooks(searchTerm);
  };

  return (
    <VStack spacing="5" color="black" width="100%" maxW="1000px" marginX="auto"> {/* Adjusted maxW here */}
      <Heading as="h1" size="xl">
        Library
      </Heading>
      <Input
        placeholder="Search for books..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <Button colorScheme="blue" onClick={handleSearch}>
        Search
      </Button>
      {loading ? (
        <SimpleGrid columns={{ base: 1, sm: 2, md: 3 }} spacing={5}>
          {[...Array(6)].map((_, index) => (
            <Box key={index} p="3" borderWidth="1px" borderRadius="md" opacity="0.5">
              {/* ... (same as before) */}
            </Box>
          ))}
        </SimpleGrid>
      ) : (
        books.length > 0 && (
          <SimpleGrid columns={{ base: 1, sm: 2, md: 3 }} spacing={5}>
            {books.slice(0, 6).map((book) => (
              <Box key={book.id} p="3" borderWidth="1px" borderRadius="md">
                {/* ... (same as before) */}
              </Box>
            ))}
          </SimpleGrid>
        )
      )}
    </VStack>
  );
};

export default Google;
