//import React, { useState, useEffect } from 'react';
//import { VStack, Heading, Input, Button, SimpleGrid, Box, Text } from '@chakra-ui/react';
//import axios from 'axios';

import React, { useState, useEffect } from 'react';
import {
  VStack,
  Heading,
  Input,
  Button,
  SimpleGrid,
  Box,
  Text,
  useColorModeValue,
} from '@chakra-ui/react';

const Library = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);

  const fetchBooks = async (query) => {
    // Dummy data for demonstration purposes
    const dummyData = [
      {
        id: 1,
        volumeInfo: {
          title: 'The Great Gatsby',
          authors: ['F. Scott Fitzgerald'],
          description:
            'The story primarily concerns the young and mysterious millionaire Jay Gatsby.',
        },
        availabilityStatus: 'Available',
      },
      {
        id: 2,
        volumeInfo: {
          title: 'To Kill a Mockingbird',
          authors: ['Harper Lee'],
          description:
            'The novel explores the irrationality of adult attitudes toward race and class.',
        },
        availabilityStatus: 'Available',
      },
      {
        id: 3,
        volumeInfo: {
          title: '1984',
          authors: ['George Orwell'],
          description: 'A dystopian novel set in Airstrip One, a province of the superstate Oceania.',
        },
        availabilityStatus: 'Available',
      },
      {
        id: 4,
        volumeInfo: {
          title: 'The Hobbit',
          authors: ['J.R.R. Tolkien'],
          description:
            'The hobbit Bilbo Baggins is persuaded by the wizard Gandalf to join a group of dwarves on a quest.',
        },
        availabilityStatus: 'Available',
      },
      {
        id: 5,
        volumeInfo: {
          title: 'Pride and Prejudice',
          authors: ['Jane Austen'],
          description:
            'The novel follows the character development of Elizabeth Bennet, the dynamic protagonist.',
        },
        availabilityStatus: 'Available',
      },
      {
        id: 6,
        volumeInfo: {
          title: 'The Catcher in the Rye',
          authors: ['J.D. Salinger'],
          description: 'A story about a teenager named Holden Caulfield in New York City.',
        },
        availabilityStatus: 'Available',
      },
    ];

    setBooks(dummyData);
  };

  useEffect(() => {
    fetchBooks('Classic'); // Default search term
  }, []);

  const handleSearch = () => {
    fetchBooks(searchTerm);
  };

  const cardBgColor = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('black', 'white');
  return (
    <VStack spacing="5" color={textColor} width="100%" maxW="800px" marginX="auto">
      <HStack spacing="5">
        <Button colorScheme="blue" onClick={() => fetchBooks('Classic')}>
          Library
        </Button>
        <Button colorScheme="green" onClick={() => fetchBooks('JavaScript')}>
          Google Books
        </Button>
      </HStack>
      <Heading as="h1" size="xl">
        Library
      </Heading>
      <Input
        placeholder="Search for books..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <Button colorScheme="blue" onClick={handleSearch}>
        Search
      </Button>
      {books.length > 0 && (
        <SimpleGrid columns={{ base: 1, sm: 2, md: 3 }} spacing={5}>
          {books.slice(0, 6).map((book) => (
            <Box
              key={book.id}
              p="3"
              borderWidth="1px"
              borderRadius="md"
              bg={cardBgColor}
              _hover={{ shadow: 'md' }}
            >
              <Heading as="h3" size="sm" mb="2" noOfLines={2}>
                {book.volumeInfo.title}
              </Heading>
              {book.volumeInfo.authors && (
                <Text fontSize="xs" color="gray.500" noOfLines={1}>
                  Author(s): {book.volumeInfo.authors.join(', ')}
                </Text>
              )}
              {book.volumeInfo.description && (
                <Text fontSize="xs" color="gray.500" noOfLines={3}>
                  {book.volumeInfo.description}
                </Text>
              )}
              {book.availabilityStatus && (
                <Text fontSize="xs" color="green.500">
                  Availability: {book.availabilityStatus}
                </Text>
              )}
            </Box>
          ))}
        </SimpleGrid>
      )}
    </VStack>
  );
};

export default Library;
